export const getUpcomingMovies = async () => {
    try {
        const response = await fetch(
            `http://localhost:8080/api/movies/tmdb/upcoming`
        );

        if (!response.ok) {
            throw new Error(response.json().message);
        }

        return await response.json();
    } catch (error) {
        throw error;
    }
};

